import React, {useState, useEffect} from 'react'
import './EditUser.css'
import { useNavigate, useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { updateUser, getSingleUser } from '../../redux/actions'



const EditUser = () => {
    const [userValues, setUserValues] = useState({
        name: '',
        email: '',
        address: ''
    })
    const [err, setError] = useState('');

    let params = useParams()
    const { user } = useSelector(state => state.data)
    
    const {name, email, address} = userValues
    
    const navigate = useNavigate()

    const dispatch = useDispatch()


    const handleChange = (e) => {
        let { name, value } = e.target
        setUserValues({...userValues, [name]: value})
   }
    
        useEffect(() => {
        dispatch(getSingleUser(params.id))
    }, [params.id, dispatch])

    useEffect(() => {
        if (user) {
                setUserValues({...userValues})
            }
        }, [])

    const handleSubmit = (e) => {
        e.preventDefault()
        if (!name || !email || !address) {
            setError('Please fill all input values')
        }
        else {
            dispatch(updateUser(userValues, params.id))
            navigate('/')
            setError('')
        }
    }

  return (
      <div className='formHolder'>
          <button onClick = {() => navigate('/')} className='back'>Go back</button>
          <form onSubmit={handleSubmit} className='formContainer'>
              {err && <p className='err'>{err}</p>}
              <div className='inputDivs'>
                <label htmlFor='name'>Name</label>
                  <input
                      className='addInput'
                      id='name'
                      type='text'
                      placeholder='Enter Name'
                      onChange={handleChange}
                      value={user.name || ''}
                  />
              </div>
              <div className='inputDivs'>
                <label htmlFor='name'>Email</label>
                  <input
                      className='addInput'
                      id='email' type='email' placeholder='Enter Email'
                      onChange={handleChange}
                      value={user.email || ''}
                  />
              </div>
              <div className='inputDivs'>
                <label htmlFor='address'>Address</label>
                  <input
                      className='addInput'
                      id='address'
                      type='text'
                      placeholder='Enter Address'
                      onChange={handleChange}
                      value={user.address || ''}
                  />
              </div>
              <div className='inputDivs'>
                <button className='formAddBtn'>Add User</button>
              </div>
          </form>
      </div>
  )
}

export default EditUser
