import React, { useState, useEffect } from 'react'
import Put from './components/Put';


const App = () => {

  const [users, setUsers] = useState([])
  const [name, setName] = useState('');
  const [email, setEmail] = useState('')
  const details = {name, email}

  const onAdd = async () => {
    await fetch('https://jsonplaceholder.typicode.com/users', {
    method: 'POST',
    body: JSON.stringify(details),
    headers: {
      'Content-type': 'application/json; charset=UTF-8',
    },
  })
    .then((response) => {
      if (response.status === 201) {
          console.log('Name is not defeince')
          return
        }
        else {
          return response.json()
        }
  })
      .then((data) => {
        setUsers(users => [...users, data])
      })
    .catch(err => console.log(err))
  };

const saveUser = e => {
  e.preventDefault()
  onAdd()   
  console.log(details)
}

  // useEffect(() => {
  //   fetch('https://jsonplaceholder.typicode.com/users/')
  //     .then((response) => response.json())
  //     .then((json) => setUsers(json));

  // })

    return (
      <div>
        <div>
          {
            users.map((user, id) => {
              return (
                <div key={id}>
                  <span>{user.name}</span>
                  <span>{ user.email}</span><br /><br />
                </div>
              )
            })
          }
        </div>

    <form onSubmit={saveUser}>
      <input type='text' placeholder='username' onChange={e => setName(e.target.value)} value={ name} />
      <input type='email' placeholder='username' onChange={e => setEmail(e.target.value)} value={email} />
      <button>Save</button>
    </form>

    <Put/>
    </div>
  )
}
export default App;
