import React, {Fragment} from 'react'
import { useParams, useLocation, Route } from 'react-router-dom'
import HighlightedQuote  from '../components/quotes/HighlightedQuote'

import Comments from '../components/comments/Comments'

const DUMMY_QUOTES = [
  {id: 1, author: 'Dabsia', text: 'Work hard and believe its going to pay offf'},
  {id: 2, author: 'Dabo', text: 'Learning React is fun'}
]


const QuoteDetail = () => {
    const params = useParams()
    const location = useLocation()

    const quote = DUMMY_QUOTES.find(quote => quote.id === params.quoteId)

    console.log(params.quoteId)
    console.log(location.pathname)

    if(!quote){
      return <p>No Quote found</p>
    }

  return (
    <Fragment>
        
        <HighlightedQuote text = {quote.text} author = {quote.author}/>
        <Route path = {`/${location.pathname}/comments`}>
          <Comments />
        </Route>
    </Fragment>
  )
}

export default QuoteDetail
